import React, { useEffect, useState } from "react";
import { Heading } from "../common/Heading";
import { fetchShelf, getCurrentUsername } from "../../services/api";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);
  const username = getCurrentUsername();

  useEffect(() => {
    if (!username) {
      console.warn("No username found. Please log in.");
      return;
    }

    fetchShelf(username)
      .then(setReadlist)
      .catch((err) => console.error("Failed to load shelf:", err));
  }, [username]);

  return (
    <section
      className="about"
      style={{
        backgroundColor: "black",
        minHeight: "100vh",
        color: "white",
        padding: "2rem",
        display: "flex",
        justifyContent: "center",
        alignItems: "flex-start",
      }}
    >
      <div
        className="readlist-box1"
        style={{
          backgroundColor: "#1c1c1c",
          padding: "2rem",
          borderRadius: "16px",
          width: "100%",
          maxWidth: "800px",
          boxShadow: "0 0 10px rgba(0,0,0,0.5)",
        }}
      >
        <Heading title={`${username}'s Readlist`} />

        {readlist.length === 0 ? (
          <p style={{ marginTop: "1rem", color: "#aaa" }}>
            Keine Bücher hinzugefügt...
          </p>
        ) : (
          <ul style={{ listStyle: "none", padding: 0, marginTop: "1.5rem" }}>
            {readlist.map((entry) => (
              <li
                key={entry.id}
                style={{
                  backgroundColor:
                    entry.shelfType === "finished" ? "#4caf50" : "#2a2a2a",
                  border:
                    entry.shelfType === "finished"
                      ? "2px solid #4caf50"
                      : "1px solid #444",
                  padding: "0.75rem 1rem",
                  borderRadius: "10px",
                  marginBottom: "0.75rem",
                  color: "white",
                }}
              >
                <strong>{entry.bookTitle}</strong> —{" "}
                <i style={{ color: "#ccc" }}>{entry.shelfType}</i>
              </li>
            ))}
          </ul>
        )}
      </div>
    </section>
  );
};

export default Readlist;
