import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { fetchBooks } from "../../services/api";

const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  // Alle Genres sammeln (ohne Duplikate)
  const genres = [...new Set(books.map(book => book.genre || "Unbekannt"))];

  return (
    <section style={{ padding: "4rem", backgroundColor: "#f9f9f9" }}>
      <h2>BÃ¼cher nach Genre</h2>
      <ul>
        {genres.map(genre => (
          <li key={genre}>
            <Link to={`/books/${encodeURIComponent(genre.toLowerCase())}`}>
              {genre}
            </Link>
          </li>
        ))}
      </ul>
    </section>
  );
};

export default Books;
