import React, { useEffect, useState } from "react";
import { Heading } from "../common/Heading";
import { fetchShelf, getCurrentUsername } from "../../services/api";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);
  const username = getCurrentUsername();

  useEffect(() => {
    if (!username) {
      setReadlist([]);  // Falls kein Nutzer, Liste leer halten
      return;
    }

    fetchShelf(username)
      .then(setReadlist)
      .catch((err) => {
        console.error("Failed to load shelf:", err);
        setReadlist([]);
      });
  }, [username]);

  if (!username) {
    return <p>Bitte zuerst einloggen, um deine Readlist zu sehen.</p>;
  }

  return (
    <section className="about" style={{
      backgroundColor: "black",
      height: "100vh",
      color: "white",
      padding: "2rem"
    }}>
      <div className="readlist-box1">
        <Heading title={`${username}'s Readlist`} />
        {readlist.length === 0 ? (
          <p>Keine Bücher hinzugefügt...</p>
        ) : (
          <ul>
            {readlist.map((entry) => (
              <li key={entry.id}>
                {entry.bookTitle} — <i>{entry.shelfType}</i>
              </li>
            ))}
          </ul>
        )}
      </div>
    </section>
  );
};
