import { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api";

const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState("A");

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const filteredBooks = books.filter((book) =>
    book.title.toUpperCase().startsWith(selectedLetter)
  );

  return (
    <>
      {/* Fixierte Buchstaben-Leiste */}
      <div
        className="top-letter-bar"
        style={{
          position: "sticky",
          top: 0,
          backgroundColor: "#fff",
          zIndex: 10,
          padding: "1rem",
          display: "flex",
          gap: "0.5rem",
          flexWrap: "wrap",
          borderBottom: "1px solid #ccc",
        }}
      >
        {alphabet.map((letter) => (
          <button
            key={letter}
            onClick={() => setSelectedLetter(letter)}
            style={{
              padding: "0.5rem 1rem",
              borderRadius: "4px",
              border: "none",
              backgroundColor: letter === selectedLetter ? "#333" : "#eee",
              color: letter === selectedLetter ? "#fff" : "#333",
              cursor: "pointer",
            }}
          >
            {letter}
          </button>
        ))}
      </div>

      {/* Bücher Abschnitt im Stil des zweiten Codes */}
      <section
        className="bookSection"
        style={{ padding: "4rem", backgroundColor: "#f9f9f9" }}
      >
        <h2>Bücher mit "{selectedLetter}"</h2>
        <div
          className="bookGrid"
          style={{ display: "flex", flexWrap: "wrap", gap: "2rem" }}
        >
          {filteredBooks.length > 0 ? (
            filteredBooks.map((book) => (
              <div
                key={book.id}
                className="bookCard"
                style={{
                  width: "200px",
                  border: "1px solid #ccc",
                  padding: "1rem",
                  borderRadius: "8px",
                  backgroundColor: "#fff",
                }}
              >
                <img
                  src={book.coverUrl}
                  alt={book.title}
                  style={{
                    width: "100%",
                    height: "auto",
                    objectFit: "cover",
                    marginBottom: "1rem",
                  }}
                />
                <h3>{book.title}</h3>
                <p>{book.author}</p>
              </div>
            ))
          ) : (
            <p>
              Keine Bücher mit dem Anfangsbuchstaben "{selectedLetter}"
              gefunden.
            </p>
          )}
        </div>
      </section>
    </>
  );
};

export default Books;
