import React, { useState, useEffect } from "react";
import { Heading } from "../common/Heading";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);

  useEffect(() => {
    const fetchReadlist = async () => {
      try {
        const res = await fetch("http://localhost:8080/shelf?username=isabella");
        if (res.ok) {
          const data = await res.json();
          setReadlist(data);
        } else {
          console.error("Failed to load shelf data");
        }
      } catch (err) {
        console.error("Error fetching shelf:", err);
      }
    };

    fetchReadlist();
  }, []);

  return (
    <>
      <section
        className="about"
        style={{
          backgroundColor: "black",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundAttachment: "fixed",
          height: "100vh",
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "relative",
        }}
      >
        <div className="readlist-box1" style={{ flex: 1 }}>
          <Heading title="Books" />
        </div>

        <div className="readlist-box2" style={{ flex: 1 }}>
          <Heading title="Meine Readlist" />
          {readlist.length === 0 ? (
            <p>Keine Bücher hinzugefügt...</p>
          ) : (
            <ul>
              {readlist.map((book, index) => (
                <li key={index}>
                  {book.bookTitle} von {book.bookAuthor}
                </li>
              ))}
            </ul>
          )}
        </div>
      </section>
    </>
  );
};

export default Readlist;
