import { useEffect, useState } from "react";
import { fetchBooks, getCurrentUsername } from "../../services/api";

const username = getCurrentUsername();

export const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

  // Gruppiere Bücher nach Anfangsbuchstabe
  const groupedBooks = books.reduce((acc, book) => {
    const firstLetter = book.title?.[0]?.toUpperCase();
    if (!firstLetter || !alphabet.includes(firstLetter)) return acc;

    if (!acc[firstLetter]) acc[firstLetter] = [];
    acc[firstLetter].push(book);
    return acc;
  }, {});

  return (
    <section className="bookSection">
      <h2>Beliebte Bücher</h2>

      {alphabet.map((letter) =>
        groupedBooks[letter] ? (
          <div key={letter} className="letter-group">
            <h3 className="letter-heading">{letter}</h3>
            <div className="bookGrid">
              {groupedBooks[letter].map((book) => (
                <div key={book.id} className="bookCard">
                  <img src={book.coverUrl} alt={book.title} />
                  <h4>{book.title}</h4>
                  <p>{book.author}</p>
                </div>
              ))}
            </div>
          </div>
        ) : null
      )}
    </section>
  );
};

export default Books;
