import { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api";

const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState("A");

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const filteredBooks = books.filter(book =>
    book.title.toUpperCase().startsWith(selectedLetter)
  );

  return (
    <>
      {/* FIXIERTE BUCHSTABEN-LEISTE GANZ OBEN */}
      <div className="top-letter-bar">
        {alphabet.map(letter => (
          <button
            key={letter}
            onClick={() => setSelectedLetter(letter)}
            className={`letter-button ${letter === selectedLetter ? "active" : ""}`}
          >
            {letter}
          </button>
        ))}
      </div>

      {/* BÜCHER INHALT */}
      <section className="book-section">
        <h2>Beliebte Bücher</h2>
        <div className="book-grid">
          {filteredBooks.length > 0 ? (
            filteredBooks.map(book => (
              <div key={book.id} className="book-card">
                <img src={book.coverUrl} alt={book.title} />
                <h3>{book.title}</h3>
                <p>{book.author}</p>
              </div>
            ))
          ) : (
            <p>Keine Bücher mit dem Anfangsbuchstaben "{selectedLetter}" gefunden.</p>
          )}
        </div>
      </section>
    </>
  );
};

export default Books;
