import React, { useEffect, useState } from "react";
import { fetchBooks, addToShelf, fetchShelf, getCurrentUsername } from "../../services/api";

const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState("A");
  const [searchTerm, setSearchTerm] = useState("");
  const [readlist, setReadlist] = useState([]);
  const username = getCurrentUsername();

  // Bücher laden
  useEffect(() => {
    fetchBooks()
      .then(setBooks)
      .catch((err) => console.error("Fehler beim Laden der Bücher:", err));
  }, []);

  // Readlist laden (wenn Nutzer da ist)
  useEffect(() => {
    if (!username) return;

    fetchShelf(username)
      .then(setReadlist)
      .catch((err) => console.error("Fehler beim Laden der Readlist:", err));
  }, [username]);

  // Bücher filtern
  const filteredBooks = books.filter((book) => {
    const matchesLetter = book.title.toUpperCase().startsWith(selectedLetter);
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase());

    if (searchTerm.trim() !== "") {
      return matchesSearch;
    }
    return matchesLetter;
  });

  // Buch zur Readlist hinzufügen
  const handleAddToReadlist = async (bookTitle) => {
    if (!username) {
      alert("Bitte zuerst registrieren oder einloggen.");
      return;
    }

    try {
      await addToShelf(username, bookTitle, "read");
      alert(`„${bookTitle}“ wurde zur Readlist hinzugefügt.`);

      // Readlist aktualisieren
      const updatedShelf = await fetchShelf(username);
      setReadlist(updatedShelf);
    } catch (error) {
      console.error("Fehler beim Hinzufügen zur Readlist:", error);
      alert("Fehler beim Hinzufügen.");
    }
  };

  return (
    <div style={{ display: "flex", padding: "1rem", gap: "2rem" }}>
      {/* Linke Seite: Bücher-Liste mit Filter */}
      <div style={{ flex: 2 }}>
        <h2>Bücher auswählen</h2>
        <div style={{ marginBottom: "1rem" }}>
          {alphabet.map((letter) => (
            <button
              key={letter}
              onClick={() => setSelectedLetter(letter)}
              style={{
                marginRight: "0.25rem",
                fontWeight: letter === selectedLetter ? "bold" : "normal",
              }}
            >
              {letter}
            </button>
          ))}
        </div>

        <input
          type="text"
          placeholder="Suchen..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          style={{ width: "100%", marginBottom: "1rem" }}
        />

        <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fill, minmax(150px, 1fr))", gap: "1rem" }}>
          {filteredBooks.length > 0 ? (
            filteredBooks.map((book) => (
              <div
                key={book.id}
                style={{
                  border: "1px solid #ccc",
                  borderRadius: "4px",
                  padding: "0.5rem",
                  textAlign: "center",
                }}
              >
                <img
                  src={book.coverUrl}
                  alt={book.title}
                  style={{ width: "100%", height: "200px", objectFit: "cover", marginBottom: "0.5rem" }}
                />
                <h4>{book.title}</h4>
                <p style={{ fontStyle: "italic", fontSize: "0.9rem" }}>{book.author}</p>
                <button
                  onClick={() => handleAddToReadlist(book.title)}
                  style={{
                    marginTop: "0.5rem",
                    padding: "0.25rem 0.5rem",
                    cursor: "pointer",
                  }}
                >
                  + Zur Readlist
                </button>
              </div>
            ))
          ) : (
            <p>
              Keine Bücher gefunden
              {searchTerm && ` mit „${searchTerm}“`}
              {!searchTerm && ` mit Anfangsbuchstaben „${selectedLetter}“`}
              .
            </p>
          )}
        </div>
      </div>

      {/* Rechte Seite: Readlist */}
      <div style={{ flex: 1, backgroundColor: "#f4f4f4", padding: "1rem", borderRadius: "8px", height: "fit-content" }}>
        <h2>Deine Readlist {username ? `(${username})` : ""}</h2>
        {username ? (
          readlist.length === 0 ? (
            <p>Keine Bücher in deiner Readlist.</p>
          ) : (
            <ul>
              {readlist.map((entry) => (
                <li key={entry.id}>
                  {entry.bookTitle} <i style={{ color: "#666" }}>({entry.shelfType})</i>
                </li>
              ))}
            </ul>
          )
        ) : (
          <p>Bitte einloggen, um deine Readlist zu sehen.</p>
        )}
      </div>
    </div>
  );
};

export default BookShelfPage;
