import React, { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api";

export const Books = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const groupedBooks = books.reduce((acc, book) => {
    const genre = book.genre || "Unbekannt";
    if (!acc[genre]) acc[genre] = [];
    acc[genre].push(book);
    return acc;
  }, {});

  return (
    <section className="bookSection" style={{ padding: "4rem", backgroundColor: "#f9f9f9" }}>
      <h1>Beliebte BÃ¼cher nach Genre</h1>
      {Object.entries(groupedBooks).map(([genre, books]) => (
        <div key={genre} style={{ marginBottom: "4rem" }}>
          <h2>{genre}</h2>
          <div className="bookGrid" style={{ display: "flex", flexWrap: "wrap", gap: "2rem" }}>
            {books.map(book => (
              <div key={book.id} style={{ width: "200px", border: "1px solid #ccc", padding: "1rem", borderRadius: "8px", backgroundColor: "#fff" }}>
                <img src={book.coverUrl} alt={book.title} style={{ width: "100%", height: "auto", objectFit: "cover" }} />
                <h3>{book.title}</h3>
                <p>{book.author}</p>
              </div>
            ))}
          </div>
        </div>
      ))}
    </section>
  );
};

export default Books;
