import React, { useState } from 'react';

const initialBooks = [
  { id: 1, title: '1984', category: 'Dystopia', finished: false },
  { id: 2, title: 'The Hobbit', category: 'Fantasy', finished: false },
  { id: 3, title: 'Sapiens', category: 'History', finished: false },
];

export const Score = () => {
  const [books, setBooks] = useState(initialBooks);
  const [scores, setScores] = useState({ Dystopia: 0, Fantasy: 0, History: 0 });

  const markAsFinished = (id) => {
    setBooks((prevBooks) =>
      prevBooks.map((book) =>
        book.id === id && !book.finished
          ? {
              ...book,
              finished: true,
            }
          : book
      )
    );

    const finishedBook = books.find((book) => book.id === id);
    if (finishedBook && !finishedBook.finished) {
      const cat = finishedBook.category;
      setScores((prevScores) => ({
        ...prevScores,
        [cat]: prevScores[cat] + 1,
      }));
    }
  };

  return (
    <div className="score-container">
      <h2>Book List</h2>
      {books.map((book) => (
        <div key={book.id} className="book-entry">
          <span>{book.title} - {book.category}</span>
          <button
            disabled={book.finished}
            onClick={() => markAsFinished(book.id)}
          >
            {book.finished ? 'Finished' : 'Mark as Finished'}
          </button>
        </div>
      ))}

      <h2>Category Scores</h2>
      <ul>
        {Object.entries(scores).map(([cat, score]) => (
          <li key={cat}>
            {cat}: {score}
          </li>
        ))}
      </ul>
    </div>
  );
};
