import React, { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api";

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [genres, setGenres] = useState([]);
  const [activeGenre, setActiveGenre] = useState("Alle");

  useEffect(() => {
    fetchBooks().then((books) => {
      setBooks(books);
      // Genres extrahieren und "Alle" als Default hinzufügen
      const uniqueGenres = [...new Set(books.map(book => book.genre))];
      setGenres(["Alle", ...uniqueGenres]);
    });
  }, []);

  // Bücher filtern je nach aktivem Tab/Genre
  const filteredBooks =
    activeGenre === "Alle"
      ? books
      : books.filter(book => book.genre === activeGenre);

  return (
    <section style={{ padding: "2rem" }}>
      <h2>Beliebte Bücher</h2>

      {/* Tabs zur Genre-Auswahl */}
      <div style={{ marginBottom: "1.5rem" }}>
        {genres.map((genre) => (
          <button
            key={genre}
            onClick={() => setActiveGenre(genre)}
            style={{
              marginRight: "0.5rem",
              padding: "0.5rem 1rem",
              cursor: "pointer",
              backgroundColor: activeGenre === genre ? "#007bff" : "#eee",
              color: activeGenre === genre ? "#fff" : "#333",
              border: "none",
              borderRadius: "4px",
            }}
          >
            {genre}
          </button>
        ))}
      </div>

      {/* Bücher anzeigen */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "1.5rem" }}>
        {filteredBooks.map((book) => (
          <div
            key={book.id}
            style={{
              width: "200px",
              border: "1px solid #ccc",
              padding: "1rem",
              borderRadius: "8px",
              backgroundColor: "#fff",
            }}
          >
            <img
              src={book.coverUrl}
              alt={book.title}
              style={{ width: "100%", height: "auto", objectFit: "cover" }}
            />
            <h3>{book.title}</h3>
            <p>{book.author}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Books;
