import React, { useState } from "react";
import { Heading } from "../common/Heading";
import { books } from "../data/datas";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);

  const addToReadlist = (book) => {
    if (!readlist.find((item) => item.id === book.id)) {
      setReadlist([...readlist, book]);
    }
  };

  return (
    <>
      <section className="about">
        <div className="container flex" style={{ gap: "40px" }}>
          {/* Bücher-Bereich (links) */}
          <div
            className="book-box"
            style={{
              flex: 1,
              borderLeft: "4px solid #444",
              paddingLeft: "20px",
              backgroundColor: "#f9f9f9",
              borderRadius: "8px",
            }}
          >
            <Heading title="Books" />
            {books.map((book) => (
              <div key={book.id} className="book-item" style={{ marginBottom: "20px" }}>
                <Heading title={book.title} />
                <p>{book.desc}</p>
                <p>Autor: {book.author}</p>
                <button onClick={() => addToReadlist(book)}>+</button>
                <button className="secondaryBtn">✓</button>
              </div>
            ))}
          </div>

          {/* Readlist-Bereich (rechts) */}
          <div
            className="readlist-box"
            style={{
              flex: 1,
              borderLeft: "4px solid #ccc",
              paddingLeft: "20px",
              backgroundColor: "#f0f0f0",
              borderRadius: "8px",
            }}
          >
            <Heading title="Meine Readlist" />
            {readlist.length === 0 ? (
              <p>Keine Bücher hinzugefügt...</p>
            ) : (
              <ul>
                {readlist.map((book) => (
                  <li key={book.id}>
                    {book.title} von {book.author}
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </section>
    </>
  );
};

export default Readlist;
