import React, { useState, useEffect } from "react";
import { Heading } from "../common/Heading";
import axios from "axios";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);

  useEffect(() => {
    const fetchReadlist = async () => {
      try {
        const response = await axios.get("http://localhost:8080/shelf?username=isabella");
        setReadlist(response.data);
      } catch (error) {
        console.error("Error loading readlist:", error);
      }
    };

    fetchReadlist();
  }, []);

  return (
    <section className="about"
      style={{
        backgroundColor: "black",
        backgroundSize: "cover",
        backgroundRepeat: "no-repeat",
        backgroundPosition: "center",
        backgroundAttachment: "fixed",
        height: "100vh",
        width: "100%",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        position: "relative",
      }}
    >
      <div className="readlist-box1" style={{ flex: 1 }}>
        <Heading title="Books" />
      </div>

      <div className="readlist-box2" style={{ flex: 1 }}>
        <Heading title="Meine Readlist" />
        {readlist.length === 0 ? (
          <p>Keine Bücher hinzugefügt...</p>
        ) : (
          <ul>
            {readlist.map((entry) => (
              <li key={entry.book.id}>
                {entry.book.title} von {entry.book.author}
              </li>
            ))}
          </ul>
        )}
      </div>
    </section>
  );
};

export default Readlist;
