import { useEffect, useState } from "react";
import { fetchBooks, addToShelf, getCurrentUsername } from "../services/api";

const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState("A");
  const [searchTerm, setSearchTerm] = useState("");
  const [readBooks, setReadBooks] = useState([]);
  const [addedToShelf, setAddedToShelf] = useState([]);
  const [score, setScore] = useState(0); // Optional: lokale Punkteverwaltung

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const handleMarkAsRead = (book) => {
    if (readBooks.includes(book.id)) return;
    setReadBooks((prev) => [...prev, book.id]);
    setScore((prev) => prev + 1); // Optional: Punkt erhöhen, wenn Buch gelesen
  };

  const handleAddToReadlist = async (book) => {
    const username = getCurrentUsername();
    if (!username) {
      console.warn("Kein Benutzer eingeloggt.");
      return;
    }

    try {
      await addToShelf(username, book.title, "read");
      setAddedToShelf((prev) => [...prev, book.id]);
      alert(`"${book.title}" wurde zur Leseliste hinzugefügt.`);
    } catch (error) {
      console.error("Fehler beim Hinzufügen zur Leseliste:", error);
      alert("Fehler beim Hinzufügen zur Leseliste.");
    }
  };

  const filteredBooks = books.filter((book) => {
    const matchesLetter = book.title.toUpperCase().startsWith(selectedLetter);
    const matchesSearch = book.title.toLowerCase().includes(searchTerm.toLowerCase());
    return searchTerm.trim() !== "" ? matchesSearch : matchesLetter;
  });

  return (
    <>
      <div className="top-letter-bar">
        {alphabet.map((letter) => (
          <button
            key={letter}
            onClick={() => setSelectedLetter(letter)}
            className={`letter-button ${letter === selectedLetter ? "active" : ""}`}
          >
            {letter}
          </button>
        ))}
      </div>

      <div className="search-bar">
        <input
          type="text"
          placeholder="Suchen..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      <p>Punkte: {score}</p> {/* Optional: Punkteanzeige */}

      <section className="book-section">
        <div className="book-grid">
          {filteredBooks.length > 0 ? (
            filteredBooks.map((book) => (
              <div key={book.id} className="book-card">
                <img src={book.coverUrl} alt={book.title} />
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <p><strong>Genre:</strong> {book.genre}</p>
                <div className="button-row">
                  <button
                    className="add-readlist-button"
                    onClick={() => handleAddToReadlist(book)}
                    disabled={addedToShelf.includes(book.id)}
                  >
                    {addedToShelf.includes(book.id) ? "✓" : "+"}
                  </button>
                  <button
                    className="read-visual-button"
                    onClick={() => handleMarkAsRead(book)}
                    disabled={readBooks.includes(book.id)}
                  >
                    ✓
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p>
              Keine Bücher gefunden
              {searchTerm && ` mit „${searchTerm}“`}
              {!searchTerm && ` mit „${selectedLetter}“`}
              .
            </p>
          )}
        </div>
      </section>
    </>
  );
};

export default Books;
