import React, { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api"; // Pfad anpassen
import { Link, Routes, Route, useParams } from "react-router-dom";

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [genres, setGenres] = useState([]);

  useEffect(() => {
    fetchBooks().then((books) => {
      setBooks(books);
      const uniqueGenres = [...new Set(books.map(book => book.genre))];
      setGenres(uniqueGenres);
    });
  }, []);

  return (
    <>
      <h2>Beliebte B端cher nach Genre</h2>

      {/* Genre Navigation */}
      <nav style={{ marginBottom: "2rem" }}>
        <Link to="/books">Alle</Link>
        {genres.map((genre) => (
          <Link key={genre} to={`/books/genre/${encodeURIComponent(genre)}`} style={{ marginLeft: "1rem" }}>
            {genre}
          </Link>
        ))}
      </nav>

      {/* Routing f端r Subseiten */}
      <Routes>
        <Route path="/" element={<BookList books={books} />} />
        <Route path="genre/:genre" element={<GenreBooks books={books} />} />
      </Routes>
    </>
  );
};

const BookList = ({ books }) => (
  <div className="bookGrid" style={{ display: "flex", flexWrap: "wrap", gap: "2rem" }}>
    {books.map(book => (
      <BookCard key={book.id} book={book} />
    ))}
  </div>
);

const GenreBooks = ({ books }) => {
  const { genre } = useParams();

  const filteredBooks = books.filter(book => book.genre === genre);

  return (
    <>
      <h3>B端cher im Genre: {genre}</h3>
      <div className="bookGrid" style={{ display: "flex", flexWrap: "wrap", gap: "2rem" }}>
        {filteredBooks.length > 0 ? (
          filteredBooks.map(book => <BookCard key={book.id} book={book} />)
        ) : (
          <p>Keine B端cher gefunden.</p>
        )}
      </div>
    </>
  );
};

const BookCard = ({ book }) => (
  <div className="bookCard" style={{ width: "200px", border: "1px solid #ccc", padding: "1rem", borderRadius: "8px", backgroundColor: "#fff" }}>
    <img src={book.coverUrl} alt={book.title} style={{ width: "100%", height: "auto", objectFit: "cover" }} />
    <h3>{book.title}</h3>
    <p>{book.author}</p>
  </div>
);

export default Books;
