import React from "react";
import { About } from "../pages/About";
import Hero from "./Hero"; // Make sure you import Hero correctly

export const Home = () => {
  return (
    <>
      <section
        className="hero"
        style={{
          backgroundImage: "url('/images/home.PNG')",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundAttachment: "fixed",
          height: "100vh",
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "relative",
        }}
      >
        <h1 style={{ color: "#e0a80d", fontSize: "60px", fontWeight: "bold" }}>
          Literatur-Score
        </h1>
        <button>go to score</button>
      </section>
      <Hero />
      <About />
    </>
  );
};

export default Home;
