import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import { fetchBooks } from "../../services/api";

const GenreBooks = () => {
  const { genre } = useParams();
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchBooks().then((data) => {
      setBooks(data);
      setLoading(false);
    });
  }, []);

  if (loading) return <p>Lade Bücher...</p>;

  // Genre aus URL zurück in "normale" Form bringen (z.B. "science fiction")
  const decodedGenre = decodeURIComponent(genre);

  // Bücher filtern
  const filteredBooks = books.filter(
    (book) => (book.genre || "Unbekannt").toLowerCase() === decodedGenre.toLowerCase()
  );

  return (
    <section style={{ padding: "4rem", backgroundColor: "#f9f9f9" }}>
      <h2>Bücher im Genre: {decodedGenre}</h2>
      <Link to="/books">← Zurück zur Übersicht</Link>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "2rem", marginTop: "1rem" }}>
        {filteredBooks.length === 0 && <p>Keine Bücher in diesem Genre gefunden.</p>}
        {filteredBooks.map(book => (
          <div key={book.id} style={{ width: "200px", border: "1px solid #ccc", padding: "1rem", borderRadius: "8px", backgroundColor: "#fff" }}>
            <img src={book.coverUrl} alt={book.title} style={{ width: "100%", height: "auto", objectFit: "cover" }} />
            <h4>{book.title}</h4>
            <p>{book.author}</p>
          </div>
        ))}
      </div>
    </section>
  );
};

export default GenreBooks;
