import React from "react";
import { About } from "../pages/About";
import { Readlist } from "../pages/Readlist";
import { Score } from "../pages/Score";
import Typewriter from "typewriter-effect"; 
import { useEffect, useState } from "react";
import { fetchBooks } from "../../services/api";
import { getCurrentUsername } from "../../services/api"; 

const username = getCurrentUsername();

const home = [
  {
    text: "Willkommen bei",
    name: "Literatur-Score",
    post: "Readinglists",
    design: "tracking your knowledge",
    desc: "Entdecke die Welt der Literatur neu.",
  },
];

export const Home = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  return (
    <>
      <section
        className="hero"
        style={{
          backgroundImage: "url('/images/home.PNG')",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundAttachment: "fixed",
          height: "100vh",
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "relative",
        }}
      >
        {home.map((val, i) => (
          <div key={i} className="heroContent">
            <h3 className="fontSize" data-aos="fade-right">
              {val.text}
            </h3>
            <h1>
              <Typewriter
                options={{
                  strings: [val.name, val.post, val.design],
                  autoStart: true,
                  loop: true,
                }}
              />
            </h1>
            <p data-aos="fade-left">{val.desc}</p>
            <button className="primaryBtn" data-aos="fade-up-right">
              go to score
            </button>
          </div>
        ))}
      </section>

      <section className="bookSection" style={{ padding: "4rem", backgroundColor: "#f9f9f9" }}>
        <h2>Beliebte BÃ¼cher</h2>
        <div className="bookGrid" style={{ display: "flex", flexWrap: "wrap", gap: "2rem" }}>
          {books.map((book) => (
            <div key={book.id} className="bookCard" style={{ width: "200px", border: "1px solid #ccc", padding: "1rem", borderRadius: "8px", backgroundColor: "#fff" }}>
              <img src={book.coverUrl} alt={book.title} style={{ width: "100%", height: "auto", objectFit: "cover" }} />
              <h3>{book.title}</h3>
              <p>{book.author}</p>
            </div>
          ))}
        </div>
      </section>

      <About />
      <Readlist />
      <Score />
    </>
  );
};

export default Home;
