import { useEffect, useState } from "react";
import { fetchBooksByGenre, fetchBooks } from "../../services/api";

export const Books = () => {
  const [genres, setGenres] = useState([]);
  const [selectedGenre, setSelectedGenre] = useState("");
  const [books, setBooks] = useState([]);
  const [sortOrder, setSortOrder] = useState(""); // "asc" oder "desc"

  // Genres initial laden
  useEffect(() => {
    fetchBooks().then((data) => {
      const uniqueGenres = Array.from(new Set(data.map((book) => book.genre)));
      setGenres(uniqueGenres);
    });
  }, []);

  // Bücher neu laden bei Genre-Wechsel
  useEffect(() => {
    if (selectedGenre) {
      fetchBooksByGenre(selectedGenre)
        .then(setBooks)
        .catch((err) => console.error("Fehler beim Laden der Bücher:", err));
    }
  }, [selectedGenre]);

  // Sortierung anwenden
  const sortedBooks = [...books].sort((a, b) => {
    if (sortOrder === "asc") return a.title.localeCompare(b.title);
    if (sortOrder === "desc") return b.title.localeCompare(a.title);
    return 0;
  });

  return (
    <section style={{ padding: "2rem" }}>
      <h2>Bücher nach Genre und Titel</h2>

      {/* Genre Dropdown */}
      <div style={{ marginBottom: "1rem" }}>
        <label style={{ marginRight: "0.5rem" }}>Genre:</label>
        <select
          value={selectedGenre}
          onChange={(e) => setSelectedGenre(e.target.value)}
        >
          <option value="">Genre auswählen...</option>
          {genres.map((genre, idx) => (
            <option key={idx} value={genre}>
              {genre}
            </option>
          ))}
        </select>
      </div>

      {/* Sortier Dropdown */}
      <div style={{ marginBottom: "2rem" }}>
        <label style={{ marginRight: "0.5rem" }}>Sortierung:</label>
        <select
          value={sortOrder}
          onChange={(e) => setSortOrder(e.target.value)}
        >
          <option value="">Keine Sortierung</option>
          <option value="asc">Titel: A–Z</option>
          <option value="desc">Titel: Z–A</option>
        </select>
      </div>

      {/* Bücherliste */}
      <div style={{ display: "flex", flexWrap: "wrap", gap: "1rem" }}>
        {sortedBooks.length === 0 && selectedGenre && <p>Keine Bücher gefunden.</p>}
        {sortedBooks.map((book) => (
          <div key={book.id} style={{ width: "200px", border: "1px solid #ccc", padding: "1rem" }}>
            <img src={book.coverUrl} alt={book.title} style={{ width: "100%" }} />
            <h3>{book.title}</h3>
            <p>{book.author}</p>
            <small><i>{book.genre}</i></small>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Books;
