import React, { useEffect, useState } from "react";
import { Heading } from "../common/Heading";
import { fetchShelf, getCurrentUsername } from "../../services/api";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);
  const username = getCurrentUsername();

  useEffect(() => {
    if (!username) {
      console.warn("Sie sind nicht angemeldet und haben keinen Zugriff");
      return;
    }

    fetchShelf(username)
      .then(setReadlist)
      .catch((err) => console.error("Laden ist Fehlgeschlagen:", err));
  }, [username]);

  return (
    <section
      className="about"
      style={{
        backgroundColor: "black",
        minHeight: "100vh",
        color: "white",
        padding: "2rem",
      }}
    >
      {/* Überschrift-Box */}
      <div className="readlist-box1">
        <Heading title={`${username}'s Readlist`} />
      </div>

      {/* Bücherliste unterhalb */}
      {readlist.length === 0 ? (
        <p>Keine Bücher hinzugefügt...</p>
      ) : (
        <ul style={{ listStyle: "none", padding: 0 }}>
          {readlist.map((entry) => (
            <li
              key={entry.id}
              style={{
                backgroundColor:
                  entry.shelfType === "finished" ? "#4caf50" : "transparent",
                border:
                  entry.shelfType === "finished"
                    ? "2px solid #4caf50"
                    : "1px solid darkgray",
                padding: "0.5rem 1rem",
                borderRadius: "8px",
                marginBottom: "0.5rem",
                color: "white",
              }}
            >
              <strong>{entry.bookTitle}</strong> —{" "}
              <i style={{ color: "#ccc" }}>{entry.shelfType}</i>
            </li>
          ))}
        </ul>
      )}
    </section>
  );
};

export default Readlist;
