import { useEffect, useState } from "react";
import {
  fetchBooks,
  addToShelf,
  getCurrentUsername,
} from "../../services/api";
import "./Books.css";

const alphabet = "ABCDEFGHIJKLMNOPQRSTUVWXYZ".split("");

export const Books = () => {
  const [books, setBooks] = useState([]);
  const [selectedLetter, setSelectedLetter] = useState("A");
  const [searchTerm, setSearchTerm] = useState("");
  const username = getCurrentUsername();

  useEffect(() => {
    fetchBooks().then(setBooks);
  }, []);

  const handleAddToReadlist = async (bookTitle) => {
    try {
      await addToShelf(username, bookTitle, "read");
      alert(`„${bookTitle}“ wurde zur Leseliste hinzugefügt!`);
    } catch (error) {
      console.error("Fehler beim Hinzufügen zur Leseliste:", error);
      alert("Fehler beim Hinzufügen zur Leseliste.");
    }
  };

  const filteredBooks = books.filter((book) => {
    const title = book.title.toUpperCase();
    const matchesLetter = title.startsWith(selectedLetter);
    const matchesSearch = book.title
      .toLowerCase()
      .includes(searchTerm.toLowerCase());
    return matchesLetter && matchesSearch;
  });

  return (
    <>
      {/* Buchstaben-Leiste */}
      <div className="top-letter-bar">
        {alphabet.map((letter) => (
          <button
            key={letter}
            onClick={() => setSelectedLetter(letter)}
            className={`letter-button ${
              letter === selectedLetter ? "active" : ""
            }`}
          >
            {letter}
          </button>
        ))}
      </div>

      {/* Suchfeld */}
      <div className="search-bar">
        <input
          type="text"
          placeholder="Nach Buchtitel suchen..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Bücher-Anzeige */}
      <section className="book-section">
        <h2>
          Bücher mit "{selectedLetter}"{" "}
          {searchTerm && `und "${searchTerm}"`}
        </h2>
        <div className="book-grid">
          {filteredBooks.length > 0 ? (
            filteredBooks.map((book) => (
              <div key={book.id} className="book-card">
                <img src={book.coverUrl} alt={book.title} />
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <button
                  className="add-button"
                  onClick={() => handleAddToReadlist(book.title)}
                >
                  ➕ Zur Leseliste
                </button>
              </div>
            ))
          ) : (
            <p>
              Keine Bücher mit "{selectedLetter}"{" "}
              {searchTerm && `und "${searchTerm}"`} gefunden.
            </p>
          )}
        </div>
      </section>
    </>
  );
};

export default Books;
