import React, { useEffect, useState } from "react";
import { Heading } from "../common/Heading";
import { fetchShelf } from "../../services/api";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);
  const [username, setUsername] = useState("");

  useEffect(() => {
    const storedUsername = localStorage.getItem("username");
    if (!storedUsername) {
      console.warn("No username found. Please log in.");
      return;
    }

    setUsername(storedUsername);

    fetchShelf(storedUsername)
      .then(setReadlist)
      .catch((err) => console.error("Failed to load shelf:", err));
  }, []);

  return (
    <section className="about" style={{
      backgroundColor: "black",
      height: "100vh",
      color: "white",
      padding: "2rem"
    }}>
      <div className="readlist-box1">
        <Heading title={username ? ` ${username}'s Readlist` : "Bitte einloggen"} />
        {!username ? (
          <p>Bitte logge dich ein, um deine Readlist zu sehen.</p>
        ) : readlist.length === 0 ? (
          <p>Keine Bücher hinzugefügt...</p>
        ) : (
          <ul>
            {readlist.map((entry) => (
              <li key={entry.id}>
                {entry.bookTitle} — <i>{entry.shelfType}</i>
              </li>
            ))}
          </ul>
        )}
      </div>
    </section>
  );
};

export default Readlist;
