import React from "react";
import { About } from "../pages/About";
import Typewriter from "typewriter-effect"; // Vergiss diesen Import nicht!

// Beispiel-Daten, falls `home` nicht importiert wird
const home = [
  {
    text: "Willkommen bei",
    name: "Literatur-Score",
    post: "Bewertungen",
    design: "Inspiration",
    desc: "Entdecke die Welt der Literatur neu.",
  },
];

export const Home = () => {
  return (
    <>
      <section
        className="hero"
        style={{
          backgroundImage: "url('/images/home.PNG')",
          backgroundSize: "cover",
          backgroundRepeat: "no-repeat",
          backgroundPosition: "center",
          backgroundAttachment: "fixed",
          height: "100vh",
          width: "100%",
          display: "flex",
          justifyContent: "center",
          alignItems: "center",
          position: "relative",
        }}
      >
        {home.map((val, i) => (
          <div key={i} className="heroContent">
            <h3 className="fontSize" data-aos="fade-right">
              {val.text}
            </h3>
            <h1>
              <Typewriter
                options={{
                  strings: [val.name, val.post, val.design],
                  autoStart: true,
                  loop: true,
                }}
              />
            </h1>
            <p data-aos="fade-left">{val.desc}</p>
            <button className="primaryBtn" data-aos="fade-up-right">
              go to score
            </button>
          </div>
        ))}
      </section>

      <About />
    </>
  );
};

export default Home;
