import React, { useState } from "react";
import { Heading } from "../common/Heading";
import { books } from "../data/datas";

export const Readlist = () => {
  const [readlist, setReadlist] = useState([]);

  const addToReadlist = (book) => {
    if (!readlist.find((item) => item.id === book.id)) {
      setReadlist([...readlist, book]);
    }
  };

  return (
    <>
      <section className="about">
        <div className="container flex">
          {books.map((book) => (
            <React.Fragment key={book.id}>
              <div className="left" data-aos="fade-down-right">
                <img src={book.cover} alt={book.title} />
              </div>
              <div className="right" data-aos="fade-down-left">
                <Heading title={book.title} />
                <p>{book.desc}</p>
                <p>Autor: {book.author}</p>
                <button onClick={() => addToReadlist(book)}>Zur Readlist</button>
                <button className="secondaryBtn"> ✓ finished </button>
              </div>
            </React.Fragment>
          ))}
        </div>

        {/* Trennstrich zwischen Bücher und Readlist */}
        <hr style={{ margin: "40px 0", border: "1px solid #ccc" }} />

        <div className="container" style={{ marginTop: "50px" }}>
          <Heading title="Meine Readlist" />
          {readlist.length === 0 ? (
            <p>Keine Bücher hinzugefügt...</p>
          ) : (
            <ul>
              {readlist.map((book) => (
                <li key={book.id}>
                  {book.title} von {book.author}
                </li>
              ))}
            </ul>
          )}
        </div>
      </section>
    </>
  );
};

export default Readlist;
